import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { themeAction } from '../store/theme-slice';
import { Link } from 'react-router-dom';
import Buttons from './Buttons';
import ProfileImage from './ProfileImage';

function NavBar(props) {
  const isDark = useSelector((state) => state.theme.isDarkMode);
  const dispatch = useDispatch();

  const toggleTheme = () => {
    dispatch(themeAction.toggleDarkMode());
  };

  const hideMenu = () => {
    if (props.toggleMenu) props.toggleMenu();
  };

  return (
    <div className="nav bg-whitewash text-stone-800 dark:bg-gray-900 dark:text-white shadow-md w-[80vw] sm:w-[300px] min-h-screen flex flex-col items-center p-4 transition-colors duration-300">
      {/* Close button for mobile */}
      <div className="self-end mb-4 sm:hidden" onClick={hideMenu}>
        <Buttons text={<div className="menu-close-icon-mob" />} width="7vw" height="7vw" />
      </div>

      {/* Profile & Name */}
      <ProfileImage />
      <div className="nav-name text-center mt-4">
        <div className="text-2xl font-bold text-terra dark:text-sea f3">John Cline</div>
        <div className="text-sm text-stone-600 dark:text-stone-300 f1">Full Stack Developer</div>
      </div>

      <div className="nav-options-cont mt-8 flex flex-col gap-4 w-full text-center font-medium">
  <Link 
    to="/" 
    onClick={hideMenu} 
    className="hover:text-azuresea dark:hover:text-terra transition-colors duration-200"
  >
    Home
  </Link>
  <Link 
    to="/education" 
    onClick={hideMenu} 
    className="hover:text-azuresea dark:hover:text-terra transition-colors duration-200"
  >
    Education
  </Link>
  <Link 
    to="/experience" 
    onClick={hideMenu} 
    className="hover:text-azuresea dark:hover:text-terra transition-colors duration-200"
  >
    Experience
  </Link>
  <Link 
    to="/projects" 
    onClick={hideMenu} 
    className="hover:text-azuresea dark:hover:text-terra transition-colors duration-200"
  >
    Projects
  </Link>
  <Link 
    to="/contact" 
    onClick={hideMenu} 
    className="hover:text-azuresea dark:hover:text-terra transition-colors duration-200"
  >
    Contact
  </Link>
</div>

      {/* Toggle Button */}
      <div className="mt-10">
      <Buttons
  text={isDark ? 'â˜€ï¸ Light Mode' : 'ðŸŒ™ Dark Mode'}
  onClick={toggleTheme}
  width="120px"
  height="40px"
  bgColor="bg-terra dark:bg-sea"
  textColor="text-white"
  hoverBg="hover:bg-[#e59472] dark:hover:bg-[#3a99c3]"
        />
      </div>
    </div>
  );
}

export default NavBar;
