import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { themeAction } from '../store/theme-slice';
import { Link } from 'react-router-dom';
import Buttons from './Buttons';
import ProfileImage from './ProfileImage';

function NavBar(props) {
  const isDark = useSelector((state) => state.theme.isDarkMode);
  const dispatch = useDispatch();

  const toggleTheme = () => {
    dispatch(themeAction.toggleDarkMode());
  };

  const hideMenu = () => {
    if (props.toggleMenu) {
      props.toggleMenu();
    }
  };


  return (
    <div className="nav bg-[#fdfcf9] text-stone-800 shadow-md w-[80vw] sm:w-[300px] min-h-screen flex flex-col items-center p-4">
      {/* Close button for mobile */}
      <div className="self-end mb-4 sm:hidden" onClick={hideMenu}>
        <Buttons text={<div className="menu-close-icon-mob" />} width="7vw" height="7vw" />
      </div>

      {/* Profile & Name */}
      <ProfileImage />
      <div className="nav-name text-center mt-4">
        <div className="text-2xl font-bold text-[#d88259] f3">John Cline</div>
        <div className="text-sm text-[#4a4a4a] f1">Full Stack Developer</div>
      </div>

      {/* Navigation Options */}
      <div className="nav-options-cont mt-8 flex flex-col gap-4 w-full text-center font-medium">
        <div className="div-option hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/">Home</Link>
        </div>
        <div className="div-option hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/education">Education</Link>
        </div>
        <div className="div-option hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/experience">Experience</Link>
        </div>
        <div className="div-option hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/projects">Projects</Link>
        </div>
        <div className="div-option hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/contact">Contact</Link>
        </div>
      </div>
    </div>
  );
}

export default NavBar;
