import Buttons from './Buttons'
import { Link } from 'react-router-dom'
import CurrentCompany from './CurrentCompany'


function HomeDetails() {
        const calculateExperience = (startDate) => {
        const start = new Date(startDate);
        const now = new Date();

        let years = now.getFullYear() - start.getFullYear();
        let months = now.getMonth() - start.getMonth() + 1;

        if (months < 0) {
            years -= 1;
            months += 12;
        }

        // Return formatted string
        if (months === 0) {
            return `${years} Years`;
        } else if (years === 0) {
            return `${months} Months`;
        }
        return `${years} Years ${months} Months`;
    }

    const experienceYears = calculateExperience("1996-11-27");

    return (
        <div style={{ display: 'flex', flexDirection: 'column' }} >
  <div className="w-full gray-100 dark:bg-gray-800 text-[#2f2f2f] dark:text-white p-6 rounded-2xl shadow-md transition-colors duration-300">
    <h1 className="main-title">John Cline</h1>
    <h2 className="subtitle">Full Stack Developer | Cloud Architect | Creative Technologist</h2>
    <p className="bio">
      I bring over a decade of cross-functional experience in full-stack development, network engineering, 
      and digital design. From building scalable cloud applications to streamlining business operations 
      with modern tech stacks, I deliver elegant solutions that balance performance and aesthetic.
    </p>
