import React from 'react';
import { motion } from 'framer-motion';

function Card2({ details, isPersonal, pno }) {
  return (
    <>
      {isPersonal ? (
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          whileInView={{ opacity: 1, scale: 1 }}
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.4, ease: 'easeInOut' }}
          className="relative max-w-2xl w-full h-52 m-2 p-2 flex flex-col shadow-md rounded-2xl  bg-slate-200 dark:bg-gray-800 dark:text-gray-100"
        >
          <div className="flex items-center justify-center h-full text-center text-slate-800 text-lg p-4">
            {details}
          </div>
        </motion.div>
      ) : (
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          whileInView={{ opacity: 1, scale: 1 }}
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.4, ease: 'easeInOut' }}
          className="relative max-w-2xl w-full h-52 m-2 p-2 flex flex-col shadow-md rounded-2xl group bg-slate-200 dark:bg-gray-800 dark:text-gray-100"
        >
          <div className="absolute inset-1 flex justify-start items-center overflow-hidden p-2">
            <motion.div
              whileHover={{ height: 'auto' }}
              className="text-base p-2 overflow-hidden text-ellipsis whitespace-nowrap group-hover:whitespace-normal group-hover:overflow-visible transition-all duration-500"
            >
              {details}
            </motion.div>
            <motion.h2
              whileHover={{ top: '-2rem', scale: 0.5, color: '#00000080' }}
              transition={{ type: 'spring', stiffness: 300 }}
              className="absolute top-[-1rem] right-2 text-[8rem] text-black/10 pointer-events-none"
            >
              {pno}
            </motion.h2>
          </div>
        </motion.div>
      )}
    </>
  );
}

export default Card2;
