import React from 'react';

function Card2({ details, isPersonal, pno }) {
  return (
    <>
      {isPersonal ? (
        <div className="relative max-w-2xl w-full h-52 m-2 p-2 flex flex-col shadow-md transition-all duration-300 transform hover:scale-105 rounded-2xl bg-slate-200 text-stone-800 dark:bg-gray-800 dark:text-gray-100">
          <div className="flex items-center justify-center h-full text-center text-lg p-4">
            {details}
          </div>
        </div>
      ) : (
        <div className="relative max-w-2xl w-full h-52 m-2 p-2 flex flex-col shadow-md transition-all duration-300 transform hover:scale-105 rounded-2xl group bg-slate-200 text-stone-800 dark:bg-gray-800 dark:text-gray-100">
          <div className="absolute inset-1 flex justify-start items-center overflow-hidden p-2">
            <div className="text-base p-2 overflow-hidden text-ellipsis whitespace-nowrap group-hover:whitespace-normal group-hover:overflow-visible transition-all duration-500">
              {details}
            </div>
            <h2 className="absolute top-[-1rem] right-2 text-[8rem] text-black/10 transition-all duration-500 pointer-events-none group-hover:top-[-2rem] group-hover:text-black/50 group-hover:text-2xl">
              {pno}
            </h2>
          </div>
        </div>
      )}
    </>
  );
}

export default Card2;
