import React from 'react';

function Card2(props) {
  const projectDet = props.details;

  return (
    <>
      {props.isPersonal ? (
        <div className="relative max-w-2xl w-full min-h-[200px] m-2 p-2 flex flex-col rounded-2xl shadow-md transition-all duration-500 ease-in-out hover:min-h-[300px] hover:scale-105 hover:shadow-lg bg-slate-200 dark:bg-gray-800 dark:text-gray-100">
          <div className="flex-1 px-4 py-6 text-lg">
            {projectDet}
          </div>
        </div>
      ) : (
        <div className="relative max-w-2xl w-full min-h-[200px] m-2 p-2 flex flex-col rounded-2xl shadow-md transition-all duration-500 ease-in-out hover:min-h-[300px] bg-slate-200 dark:bg-gray-800 dark:text-gray-100 overflow-hidden">
          <div className="absolute inset-1 bg-slate-200 dark:bg-gray-800 rounded-2xl flex items-start p-4">
            <div className="flex-1 text-base p-2 truncate">
              {projectDet}
            </div>
            <h2 className="absolute right-4 top-[-2vh] text-[8rem] text-black/10 pointer-events-none transition-all duration-500 group-hover:text-black/50 group-hover:top-[-30px]">
              {props.pno}
            </h2>
          </div>
        </div>
      )}
    </>
  );
}

export default Card2;
