import React from 'react'
import Buttons from './Buttons'
import { Link } from 'react-router-dom'
import CurrentCompany from './CurrentCompany'


function HomeDetails() {
        const calculateExperience = (startDate) => {
        const start = new Date(startDate);
        const now = new Date();

        let years = now.getFullYear() - start.getFullYear();
        let months = now.getMonth() - start.getMonth() + 1;

        if (months < 0) {
            years -= 1;
            months += 12;
        }

        // Return formatted string
        if (months === 0) {
            return `${years} Years`;
        } else if (years === 0) {
            return `${months} Months`;
        }
        return `${years} Years ${months} Months`;
    }

    const experienceYears = calculateExperience("1996-11-27");

    return (
        <div style={{ display: 'flex', flexDirection: 'column' }} >
            {/* <div className='home-boy-img'></div> */}
            <div className="home-details-container">
      <h1 className="main-title">John Cline</h1>
      <h2 className="subtitle">Full Stack Developer | Cloud Architect | Creative Technologist</h2>
      <p className="bio">
        I bring over a decade of cross-functional experience in full-stack development, network engineering, 
        and digital design. From building scalable cloud applications to streamlining business operations 
        with modern tech stacks, I deliver elegant solutions that balance performance and aesthetic.
      </p>
      <p className="location">üìç Fort Lauderdale, FL | üá∫üá∏ USA üáÆüáπ Italy</p>
    </div>
            <CurrentCompany />
            <div className='frame f1' style={{ width: '100%', height: '10vh', marginTop: '5vh', padding: '10px' }}>
                Experience: {experienceYears}<br />
                Skills: JAVASCRIPT, C#, REACT JS, PYTHON3, <br />
                Azure, AWS
                <br />EXPRESS JS, JQUERY, HTML, CSS,
                SQL, JIRA, MONGODB, GIT<br />
            </div>
            <div className='df' style={{ width: '100%', marginTop: '5vh' }}>
                <div className='neu-btn cv-download' style={{ width: '10vw', marginBottom: '3vh' }}>
                    <Link to="/JCline_Res_IT25.pdf" target="_blank" download>Download CV</Link>
                </div>
            </div>

        </div>
    )
}

export default HomeDetails
