import React, { useEffect, useRef, useState } from 'react';

function BodyCard({ header, skills, className }) {
  const [visibleBars, setVisibleBars] = useState([]);
  const refs = useRef([]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const idx = Number(entry.target.dataset.index);
            setVisibleBars(prev => [...new Set([...prev, idx])]);
          }
        });
      },
      { threshold: 0.3 }
    );

    refs.current.forEach(ref => ref && observer.observe(ref));

    return () => observer.disconnect();
  }, []);

  return (
    <div
      className={`frame2 ${className}`}
      style={{
        width: '20%',
        flex: '1 1 2 50px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        padding: '20px',
        boxSizing: 'border-box',
      }}
    >
      <div className="bodycard-header f2 dark:text-sea" style={{ marginBottom: '25px' }}>
        {header.toUpperCase()}
      </div>
      <ul style={{ width: '100%' }}>
        {skills.map((skill, index) => (
          <div key={index} className="skill-bar-wrapper">
            <div className="skill-title">{skill.name}</div>
            <div
              className="skill-bar"
              ref={(el) => (refs.current[index] = el)}
              data-index={index}
            >
              <div
                className={`skill-fill ${
                  visibleBars.includes(index) ? 'visible' : ''
                }`}
                style={{
                  '--target-width': skill.level,
                  backgroundColor: skill.color,
                }}
              />
            </div>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default BodyCard;
