import React from 'react';

function Card2(props) {
  const projectDet = props.details;

  return (
    <>
      {props.isPersonal ? (
       <div className="relative max-w-2xl w-full m-2 p-4 flex flex-col rounded-2xl shadow-md bg-slate-200 dark:bg-gray-800 dark:text-gray-100 transition-transform hover:scale-105 hover:shadow-lg duration-500 ease-in-out">

          <div className="text-lg">
            {projectDet}
          </div>
        </div>
      ) : (
        <div className="relative max-w-2xl w-full m-2 p-4 flex flex-col rounded-2xl shadow-md bg-slate-200 dark:bg-gray-800 dark:text-gray-100 transition-all duration-500 ease-in-out overflow-hidden">
          <div className="relative w-full flex flex-col items-start">
            <div className="text-base mb-4">
              {projectDet}
            </div>
            <h2 className="absolute right-4 top-[-1rem] text-[8rem] text-black/10 pointer-events-none select-none">
              {props.pno}
            </h2>
          </div>
        </div>
      )}
    </>
  );
}

export default Card2;
