import React from 'react'
import '../Styles/NavBar.css'
import { Link } from 'react-router-dom'
import { useSelector, useDispatch } from "react-redux";
import {themeAction } from '../store/theme-slice'
import ProfileImage from './ProfileImage';
import Buttons from './Buttons';
function NavBar(props) {
    const isDark = useSelector((state) => state.theme.isDarkMode);
    const dispatch = useDispatch();

    const toggleTheme = () => {
      dispatch(themeAction.toggleDarkMode());
    };

    const hideMenu = () => {
      props.toggleMenu();
    };

    return (
        <div className='nav'>
            <div className="fixed top-0 left-0 w-full bg-[#fdfcf9] shadow-md shadow-[#00000010] px-6 py-4 z-50 flex flex-col md:flex-row md:items-center md:justify-between">
      {/* Mobile close button */}
      <div className="md:hidden self-end mb-2" onClick={hideMenu}>
        <Buttons text={<div className="menu-close-icon-mob" />} width="7vw" height="7vw" />
      </div>

      {/* Profile image + name */}
      <div className="flex flex-col md:flex-row md:items-center gap-4">
        <ProfileImage />
        <div className="nav-name">
          <div className="text-2xl font-bold text-[#d88259] f3">John Cline</div>
          <div className="text-sm text-stone-700 f1">Full Stack Developer</div>
        </div>
      </div>

      {/* Navigation links */}
      <div className="nav-options-cont flex flex-col md:flex-row gap-4 md:gap-6 mt-4 md:mt-0 text-stone-700 font-medium">
        <div className="div-option cursor-pointer hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/">Home</Link>
        </div>
        <div className="div-option cursor-pointer hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/education">Education</Link>
        </div>
        <div className="div-option cursor-pointer hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/experience">Experience</Link>
        </div>
        <div className="div-option cursor-pointer hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/projects">Projects</Link>
        </div>
        <div className="div-option cursor-pointer hover:text-[#5db2d4]" onClick={hideMenu}>
          <Link to="/contact">Contact</Link>
        </div>
      </div>
    </div>
    </div>
  );
}

export default NavBar
