import React, { useState } from 'react';
import '../Styles/contact.css';
import emailjs from 'emailjs-com';
import EMAIL_CONFIG from '../Utils/email'; // or wherever you store email.js

import ContactOptions from '../Component/ContactOptions';

function Contact() {
  const [vname, setVname] = useState("");
  const [vemail, setVemail] = useState("");
  const [vsubject, setVsubject] = useState("");
  const [vdesc, setVdesc] = useState("");
  const [status, setStatus] = useState(""); // "success" | "error" | ""

  const handleSend = () => {
    setStatus(""); // Clear old status

    if (!vname || !vemail || !vdesc) {
      setStatus("error");
      return;
    }

    emailjs.send(
      EMAIL_CONFIG.SERVICE_ID,
      EMAIL_CONFIG.TEMPLATE_ID,
      {
        from_name: vname,
        from_email: vemail,
        subject: vsubject,
        message: vdesc,
      },
      EMAIL_CONFIG.USER_ID
    )

    .then(() => {
      setStatus("success");
      setVname(""); setVemail(""); setVsubject(""); setVdesc("");
    })
    .catch(() => {
      setStatus("error");
    });
  };

  return (
    <div className='dp contact df' style={{ flexDirection: 'column' }}>
      <div className='f4 df text-shadow text-slate-700 dark:text-sea'>CONTACT ME</div>
      <div className='boxc'> 
        <div className='contact-form'>
          <div className="fields dfc">
            <div className="username">
              <input type="text" className="user-input" placeholder="Your name" value={vname} onChange={e => setVname(e.target.value)} />
            </div>
            <div className="password">
              <input type="email" className="user-input" placeholder="Your email" value={vemail} onChange={e => setVemail(e.target.value)} />
            </div>
            <div className="username">
              <input type="text" className="user-input" placeholder="Subject" value={vsubject} onChange={e => setVsubject(e.target.value)} />
            </div>
            <div className="text-area">
              <textarea className="user-input" placeholder="Message" value={vdesc} onChange={e => setVdesc(e.target.value)} />
            </div>

            <div className="submitBtn">
              <ContactOptions />
              <div className='neu-btn submit-btn' onClick={handleSend}>Send Now</div>
            </div>

            {status === "error" && <div className="message-error">Oops! Please fill out all fields correctly.</div>}
            {status === "success" && <div className="message-success">âœ… Message sent successfully!</div>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Contact;
