import React, { useEffect, useRef, useState } from 'react';

function BodyCard({ header, skills, className }) {
    const [visibleBars, setVisibleBars] = useState([]);
    const refs = useRef([]);

    useEffect(() => {
      const observer = new IntersectionObserver(
        entries => {
          entries.forEach(entry => {
            if (entry.isIntersecting) {
              const idx = Number(entry.target.dataset.index);
              setVisibleBars(prev => [...new Set([...prev, idx])]);
            }
          });
        },
        { threshold: 0.3 }
      );

      refs.current.forEach(ref => ref && observer.observe(ref));

      return () => observer.disconnect();
    }, []);

    return (
      <div className='body-card'>
        <h3>{header}</h3>
        {skills.map((skill, i) => (
          <div key={i} className="skill-bar-wrapper">
            <div className="skill-title">{skill.name}</div>
            <div className="skill-bar" ref={el => (refs.current[i] = el)} data-index={i}>
              <div
                className={`skill-fill ${visibleBars.includes(i) ? 'visible' : ''}`}
                style={{ '--target-width': skill.level, backgroundColor: skill.color }}
              />
            </div>
          </div>
        ))}
      </div>
    );
  }

export default BodyCard
